// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import "./ISnookState.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
contract SnookState is ISnookState, Initializable {

  mapping (uint => Descriptor) private _descriptors;

  address private _game;
  address private _skinRewards;
  address private _afterdeath;
  address private _sge;

  function getSnookGameAddress() external override view returns (address) {
    return _game;
  }

  function getSkinRewardsAddress() external override view returns (address) {
    return _skinRewards;
  }

  function getAfterdeathAddress() external override view returns (address) {
    return _afterdeath;
  }

  function getSGEAddress() external override view returns (address) {
    return _sge;
  }

  function initialize(
    address game,
    address skinRewards,
    address afterdeath,
    address sge
  ) initializer public {
    _game = game;
    _skinRewards = skinRewards;
    _afterdeath = afterdeath;
    _sge = sge;
  }

  function initSGE(address sge) public {
    require(msg.sender == 0x23fCA1B66C39F2B6ef226B9f0F47686E27e0dFa0, 'Not deployer');
    _sge = sge;
  }

  modifier onlyGameContracts {
    require(
      msg.sender == _game || 
      msg.sender == _skinRewards || 
      msg.sender == _sge ||
      msg.sender == _afterdeath, 
      'SnookState: Not game contracts'
    );
    _;
  }

  function getDescriptor(uint tokenId) onlyGameContracts external override view returns(Descriptor memory) {
    return _descriptors[tokenId];
  }

  function setDescriptor(uint tokenId, Descriptor calldata descriptor) onlyGameContracts external override {
    _setDescriptor(tokenId, descriptor);

  }
  function deleteDescriptor(uint tokenId) onlyGameContracts external override {
    delete _descriptors[tokenId];
  }
  function _setDescriptor(uint tokenId, Descriptor calldata descriptor) internal {
    _descriptors[tokenId] = descriptor;
  }

  function setDescriptors(uint[] calldata tokenIds, Descriptor[] calldata descriptors) onlyGameContracts external override {
    require(tokenIds.length == descriptors.length, 'SnookState: invalid lengths');
    for (uint i=0; i<tokenIds.length; i++) {
      _setDescriptor(tokenIds[i], descriptors[i]);
    }
  }
}

contracts/StakingRewards.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;
import "@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "./ISkillToken.sol";
import "./IStakingRewards.sol";

contract StakingRewards is IStakingRewards, AccessControlEnumerableUpgradeable, PausableUpgradeable {
  bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

  uint private _secondsInDay;
  uint private _initialSkillSupplyInWei; 
  uint private _maxStakingPeriodInDays; 
  uint private _minStakingPeriodInDays; 

  uint constant CentiPercent = 100 * 10**2;
  uint private _interestRatePerDayInCentipercents; // 1 = 0.01 %

  uint private _minNumberOfStakers;
  uint private _minStakingValueCoef;
  uint private _cmax; // re-calculated every _maxStakingPeriod
  uint private _cmin;
  uint private _prevInitTime;
  uint private _burningRateInPercents; //  1 = 1%
  ISkillToken private _skill;
  address _treasury;

  mapping (address => DepositRecord[]) private _beneficiaryDepositRecords;

  bool private _burnExecuted;

  function initialize(
    address skill, 
    address treasury,
    uint minStakingPeriodInDays, 
    uint minNumberOfStakers,
    uint interestRatePerDayInCentipercents, 
    uint initialSkillSupplyInWei,
    uint minStakingValueCoef, // factor by which Cmax is devided to get Cmin, Cmin = Cmax / minStatkingValueCoef
    uint burningRateInPercents,
    uint secondsInDay,
    address adminAccount
  ) initializer public 
  {
    require(
      interestRatePerDayInCentipercents > 0 &&  
      minNumberOfStakers > 0 && 
      initialSkillSupplyInWei > 0 &&
      minStakingValueCoef > 0, "StakingRewards: Zero values not allowed"
    );


    __AccessControlEnumerable_init();
    __Pausable_init();

    _skill = ISkillToken(skill);
    _treasury = treasury;
    _minStakingPeriodInDays = minStakingPeriodInDays;
    _minNumberOfStakers = minNumberOfStakers;
    _interestRatePerDayInCentipercents = interestRatePerDayInCentipercents;
    _initialSkillSupplyInWei = initialSkillSupplyInWei;
    _minStakingValueCoef = minStakingValueCoef;
    _burningRateInPercents = burningRateInPercents;
    _secondsInDay = secondsInDay;

    _setupRole(DEFAULT_ADMIN_ROLE, adminAccount);
    _setupRole(PAUSER_ROLE, adminAccount);
  }

  function getSecondsInDay() external override view returns (uint) {
    return _secondsInDay;
  } 

  function getInitialSNKSupplyInWei() external override view returns (uint) {
    return _initialSkillSupplyInWei;
  }

  function getMaxStakingPeriodInDays() external override view returns (uint) {
    return _maxStakingPeriodInDays;
  }

  function getMinStakingPeriodInDays() external override view returns (uint) {
    return _minStakingPeriodInDays;
  }

  function getInterestRatePerDayInCentipercents() external override view returns (uint) {
    return _interestRatePerDayInCentipercents;
  }

  function getMinNumberOfStakers() external override view returns (uint) {
    return _minNumberOfStakers;
  }

  function getMinStakingValueCoef() external override view returns(uint) {
    return _minStakingValueCoef;
  }

  function getPrevInitTime() external override view returns(uint) {
    return _prevInitTime;
  }

  function getBurningRateInPercents() external override view returns(uint) {
    return _burningRateInPercents;
  }

  // SNK is rebrandad name of SKILL
  function getSNKAddress() external override view returns (address) {
    return address(_skill);
  }

  function getTreasuryAddress() external override view returns (address) {
    return address(_treasury);
  }

  modifier onlyTreasury() {
    require(msg.sender == _treasury, 'StakingRewards: Not treasury');
    _;
  }

  function init(uint maxStakingPeriodInDays) external override onlyTreasury() whenNotPaused() {

    _prevInitTime = block.timestamp;
    _maxStakingPeriodInDays = maxStakingPeriodInDays; // = tmax
    uint T = _skill.balanceOf(address(this));
    uint S = _skill.totalSupply();
    uint S0 = _initialSkillSupplyInWei;

    _cmax = S0 * T  * CentiPercent / _interestRatePerDayInCentipercents / _minNumberOfStakers / S / maxStakingPeriodInDays;
    if (_cmax > T) { 
      _cmax = T;
    }
    _cmin = _cmax / _minStakingValueCoef;
    emit PeriodStart(_cmin, _cmax, T, S);
  }

  function getDepositLimits() external override view returns(uint, uint) {
    return (_cmin, _cmax);
  }

  function computeRewards(uint amount, uint periodInDays) external override view 
    returns (uint rewards, uint rewardsToBurn, uint rewardsToPay) 
  {
    return _computeRewards(amount, periodInDays);
  }

  function _computeRewards(uint amount, uint periodInDays) internal view 
    returns (uint rewards, uint rewardsToBurn, uint rewardsToPay) 
  {

    rewards = amount * periodInDays * _skill.totalSupply() * _interestRatePerDayInCentipercents / CentiPercent / _initialSkillSupplyInWei;
    rewardsToBurn = rewards * _burningRateInPercents / 100;
    rewardsToPay = rewards - rewardsToBurn;
  }


  function deposit(uint amount, uint periodInDays) external whenNotPaused() override {
    require(block.timestamp > _prevInitTime && block.timestamp < _prevInitTime + _maxStakingPeriodInDays * _secondsInDay, 'Reward cycle is not initialized' );
    require(periodInDays >= _minStakingPeriodInDays && periodInDays <= _maxStakingPeriodInDays, 'Invalid staking period');
    require(amount >= _cmin && amount <= _cmax, 'Invalid staking amount'); 

    uint releaseTime = block.timestamp + periodInDays * _secondsInDay;
    address beneficiary = msg.sender;
    TokenTimelock tokenTimelock = new TokenTimelock(IERC20(address(_skill)), beneficiary, releaseTime);
    (uint rewards, uint rewardsToBurn, uint rewardsToPay) = _computeRewards(amount, periodInDays);

    require(_skill.transferFrom(msg.sender, address(tokenTimelock), amount), 'Not enough funds');
    _skill.burn(address(this), rewardsToBurn);
    _skill.transfer(address(tokenTimelock), rewardsToPay);

    DepositRecord memory depositRecord = DepositRecord(
      tokenTimelock,
      amount,
      rewards,
      rewardsToBurn,
      rewardsToPay
    );

    _beneficiaryDepositRecords[beneficiary].push(depositRecord);

    emit Deposit(
      beneficiary, 
      address(tokenTimelock), 
      amount, 
      rewards,
      rewardsToBurn,
      rewardsToPay
    );
  }

  function getDepositRecords(address beneficiary) external view override returns (DepositRecord[] memory) {
    return _beneficiaryDepositRecords[beneficiary];
  }

  function pause() external override onlyRole(PAUSER_ROLE) whenNotPaused() {
    _pause();
  }

  function unpause() external override onlyRole(PAUSER_ROLE) whenPaused() {
    _unpause();
  }

  // for adjusting vesting
  function burnFromOldVesting() onlyRole(PAUSER_ROLE) whenNotPaused() external {
    require(_burnExecuted == false, 'Already executed');
    address oldVestingAddress = 0x73F45FA6f81535596600b7C2C93F4b5A71Cb55E8;
    uint balanceOfOldVesting = _skill.balanceOf(oldVestingAddress); 
    _skill.burn(oldVestingAddress, balanceOfOldVesting);
    _burnExecuted = true;
  }
}

contracts/Vesting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract Vesting {
    using SafeERC20 for IERC20;
    event Released( address indexed beneficiary, uint256 amount);
    enum VestingPlan {
        Team,
        SeedRound,
        PrivateRound,
        StrategicPartners,
        Advisors,
        Treasury,
        Ecosystem,
        LiquidityPool
    }
    struct TokenAward {
        uint256 amount;
        uint256 released;
        VestingPlan vestingPlan;
    }
    uint256 constant public RESOLUTION = 1000000;
    // Tracks the token awards for each user (user => award)
    mapping(address => TokenAward) public awards;
    uint256 immutable public secondsInDay;
    uint256 immutable public vestingStart;
    IERC20 public targetToken;

    uint256 constant public cliffPeriodInMonthsTeamSeed = 2;
    uint256 constant public cliffPeriodInMonthsPrivate = 2;
    uint256 constant public cliffPeriodInMonthsStrategic = 3;
    uint256 constant public cliffPeriodInMonthsAdvisors = 6;

    uint256 constant public deltaPeriodInMonthsTeamSeed = 3;
    uint256 constant public deltaPeriodInMonthsPrivate = 2;
    uint256 constant public deltaPeriodInMonthsStrategic = 2;
    uint256 constant public deltaPeriodInMonthsAdvisors = 6;

    uint256 constant public quotaPercentagePrivate = 10 * 10000;  // 10 %
    uint256 constant public quotaPercentageStrategic = 7.6923 * 10000; // 7.6923 %
    uint256 constant public quotaPercentageAdvisors = 20 * 10000; //  20 %
    uint256 constant public quotaPercentageLiquidityPool = 25 * 10000; // 25 %

    uint256 constant public vestingEventsLiquidityPoolCount = 10;
    uint256 constant public sumTotalMonthsForVesting = 666; // sum(1:36)
    uint256 constant public totalMonthsOfVesting = 36;

    address[] public beneficiariesAlreadyReleased;
    uint256[] public amountsAlreadyReleased;

    constructor(
     IERC20 _targetToken,
     address[] memory beneficiaries,
     TokenAward[] memory _awards,
     uint _secondsInDay,
     uint256 totalAwards,

     uint256 vestingStartTimestamp,
     address[] memory _beneficiariesAlreadyReleased,
     uint256[] memory _amountsAlreadyReleased 

     ) {

        require(beneficiaries.length == _awards.length, 'invalid beneficiaries-award params');
        require(beneficiariesAlreadyReleased.length == amountsAlreadyReleased.length, "invalid already-released params");

        targetToken = _targetToken;
        initAwards(beneficiaries, _awards, totalAwards);
        secondsInDay = _secondsInDay;

        beneficiariesAlreadyReleased = _beneficiariesAlreadyReleased;
        amountsAlreadyReleased = _amountsAlreadyReleased;
        // vesting start timestamp should be equal to the current vesting contract deploy timestamp
        vestingStart = vestingStartTimestamp;
        adjustAwards();
    }

    function adjustAwards() internal {
      for (uint i=0; i<beneficiariesAlreadyReleased.length; i++) {
        TokenAward storage award = awards[beneficiariesAlreadyReleased[i]];
        award.released += amountsAlreadyReleased[i];
      }
    }

    function release(address beneficiary) external {
        TokenAward storage award = awards[beneficiary];
        uint256 awardAmount = award.amount;
        uint256 unreleased = getReleasableAmount(beneficiary);
        require(unreleased > 0, "Nothing to release");
        award.released += unreleased;
        require(award.released <= awardAmount, "cannot release more than allocated");
        targetToken.safeTransfer(beneficiary, unreleased);
        emit Released(beneficiary, unreleased);
    }

    function getReleasableAmount(address beneficiary) public view returns (uint256) {
        uint256 monthsPassed = (block.timestamp - vestingStart) / (secondsInDay * 30);
        uint256 vestedAmount;
        TokenAward storage award = awards[beneficiary];
        uint256 awardAmount = award.amount;
        VestingPlan vestingPlan = award.vestingPlan;

        if (vestingPlan <= VestingPlan.SeedRound) {
            vestedAmount = getAmountForTeamSeed(awardAmount, monthsPassed);
        } else if (
            vestingPlan > VestingPlan.SeedRound &&
            vestingPlan <= VestingPlan.Advisors
        ) {
            uint256 percentage;
            uint256 cliffPeriodInMonths;
            uint256 deltaPeriodInMonths;
            if (vestingPlan == VestingPlan.PrivateRound){
                percentage = quotaPercentagePrivate;
                cliffPeriodInMonths = cliffPeriodInMonthsPrivate;
                deltaPeriodInMonths = deltaPeriodInMonthsPrivate;
            } else if (vestingPlan == VestingPlan.StrategicPartners){
                percentage = quotaPercentageStrategic;
                cliffPeriodInMonths = cliffPeriodInMonthsStrategic;
                deltaPeriodInMonths = deltaPeriodInMonthsStrategic;
            } else {
                percentage = quotaPercentageAdvisors;
                cliffPeriodInMonths = cliffPeriodInMonthsAdvisors;
                deltaPeriodInMonths = deltaPeriodInMonthsAdvisors;
            }
            vestedAmount = getAmountForPrivateStrategicAdvisors(awardAmount, monthsPassed, percentage, cliffPeriodInMonths, deltaPeriodInMonths);
        } else if (
            vestingPlan > VestingPlan.Advisors &&
            vestingPlan <= VestingPlan.Ecosystem
        ) {
            vestedAmount = getAmountForTreasuryEcosystem(awardAmount, monthsPassed, vestingPlan);
        } else if (vestingPlan == VestingPlan.LiquidityPool) {
            vestedAmount = getAmountForLiquidityPool(awardAmount, monthsPassed, quotaPercentageLiquidityPool);
        } else {
            vestedAmount = 0;
        }

        uint256 amountToWithdraw = vestedAmount - award.released;
        return amountToWithdraw;
    }

    function getAmountForTeamSeed(uint256 awardAmount, uint256 monthsPassed)
        public
        pure
        returns (uint256)
    {
        if (monthsPassed < cliffPeriodInMonthsTeamSeed) {
            return 0;
        }
        uint256 accumulatedPercentage;
        if (monthsPassed > cliffPeriodInMonthsTeamSeed) {
            accumulatedPercentage += 5 * 10000;
        }
        if (monthsPassed > cliffPeriodInMonthsTeamSeed + 1 * deltaPeriodInMonthsTeamSeed) {
            accumulatedPercentage += 7.5 * 10000;
        }
        if (monthsPassed > cliffPeriodInMonthsTeamSeed + 2 * deltaPeriodInMonthsTeamSeed) {
            accumulatedPercentage += 10 * 10000;
        }
        if (monthsPassed > cliffPeriodInMonthsTeamSeed + 3 * deltaPeriodInMonthsTeamSeed) {
            accumulatedPercentage += 15 * 10000;
        }
        if (monthsPassed > cliffPeriodInMonthsTeamSeed + 4 * deltaPeriodInMonthsTeamSeed) {
            accumulatedPercentage += 15 * 10000;
        }
        if (monthsPassed > cliffPeriodInMonthsTeamSeed + 5 * deltaPeriodInMonthsTeamSeed) {
            accumulatedPercentage += 10 * 10000;
        }
        if (monthsPassed > cliffPeriodInMonthsTeamSeed + 6 * deltaPeriodInMonthsTeamSeed) {
            accumulatedPercentage += 10 * 10000;
        }
        if (monthsPassed > cliffPeriodInMonthsTeamSeed + 7 * deltaPeriodInMonthsTeamSeed) {
            accumulatedPercentage += 10 * 10000;
        }
        if (monthsPassed > cliffPeriodInMonthsTeamSeed + 8 * deltaPeriodInMonthsTeamSeed) {
            accumulatedPercentage += 10 * 10000;
        }
        if (monthsPassed > cliffPeriodInMonthsTeamSeed + 9 * deltaPeriodInMonthsTeamSeed) {
            accumulatedPercentage += 7.5 * 10000;
        }

        return ((awardAmount * accumulatedPercentage) / RESOLUTION);
    }

    function getAmountForPrivateStrategicAdvisors(uint256 awardAmount, uint256 monthsPassed, uint256 percentage, uint256 cliffPeriodInMonths, uint256 deltaPeriodInMonths)
        public
        pure
        returns (uint256)
    {
        
        if (monthsPassed < cliffPeriodInMonths ) {
            return 0;
        }

        uint256 amountToWithdraw =
            (( (monthsPassed - cliffPeriodInMonths + deltaPeriodInMonths) / deltaPeriodInMonths) *
                awardAmount *
                percentage) / (RESOLUTION);

        return amountToWithdraw < awardAmount ? amountToWithdraw : awardAmount;

        
    }

    function getAmountForTreasuryEcosystem(uint256 awardAmount, uint256 monthsPassed, VestingPlan vestingPlan) public pure returns (uint256) {
        uint256 amountToWithdraw;
        for (uint256 i = 0; i <= monthsPassed; i++){
            uint256 coefficient = 0;
            if (vestingPlan == VestingPlan.Ecosystem){
                coefficient = i + 1;
            } else {
                coefficient = totalMonthsOfVesting - i;
            }
            amountToWithdraw += (awardAmount / sumTotalMonthsForVesting) * coefficient;
        }
        return amountToWithdraw;
    }

    function getAmountForLiquidityPool(uint256 awardAmount, uint256 monthsPassed, uint256 percentage)
        public
        pure
        returns (uint256)
    {
        uint256 amountToWithdraw = (percentage * awardAmount) / RESOLUTION ;
        if (monthsPassed >= 1) {
            uint256 amount =
                ((monthsPassed) * awardAmount * ((RESOLUTION - percentage) / vestingEventsLiquidityPoolCount)) / RESOLUTION;
            amountToWithdraw += amount;
        }
        amountToWithdraw = amountToWithdraw > awardAmount ? awardAmount : amountToWithdraw;
        return amountToWithdraw;
    }
     
    function initAwards(address[] memory  beneficiaries, TokenAward[] memory _awards, uint256 totalAwards) internal{
        uint256 _totalAwards = 0;
        for(uint256 i = 0; i < beneficiaries.length; i++){
            require(awards[beneficiaries[i]].amount == 0, "duplicated beneficiary");
            awards[beneficiaries[i]] = _awards[i];
            _totalAwards += _awards[i].amount;
        }
        require(_totalAwards == totalAwards, "different awards amount");
    }
}

