contracts/SpecialSkinRewards.sol.content:
// SPDX-License-Identifier: MIT

// Test constract 
pragma solidity ^0.8.0;
import './SkillToken.sol';
import './SnookToken.sol';
import './SnookGame.sol';
import "@openzeppelin/contracts/token/ERC20/utils/TokenTimelock.sol";


// https://ethereum.stackexchange.com/questions/68934/how-to-manage-big-loops-in-solidity
contract SpecialSkinRewards {
  uint private _periodicity;
  SkillToken private _skill;
  SnookToken private _snook;
  SnookGame private _game;
  mapping (address => uint) private _beneficiaryRewards;


  constructor(address skill, address snook, address game, uint periodicity) {
    _periodicity = periodicity;
    _skill = SkillToken(skill);
    _snook = SnookToken(snook);
    _game = SnookGame(game);
  }


  // After calling this function the entire balance of the contract is used.
  // So after treasury allocation, only a single call to the function is possible,
  // other calls will be reverted because of zero balance.  
  function sendRewards() public {
    // TODO: https://ethereum.stackexchange.com/questions/68934/how-to-manage-big-loops-in-solidity
    uint balance = _skill.balanceOf(address(this));
    require(balance > 0, 'No funds in the reward contract');
    uint totalStars = 0;

    // Not count skins of dead tokens 

    // calculate totals
    for (uint i=0; i<_snook.totalSupply(); i++) {
      uint tokenId = _snook.tokenByIndex(i);
      (,,uint stars,uint deathTime) = _game.describe(tokenId);
      if (deathTime != 0) {
        continue;
      }
      totalStars += stars;
    }

    // calculate reward PER token owner
    for (uint i=0; i<_snook.totalSupply(); i++) {
      uint tokenId = _snook.tokenByIndex(i);
      address beneficiary = _snook.ownerOf(tokenId);
      (,,uint stars,uint deathTime) = _game.describe(tokenId);
      if (deathTime != 0) {
        continue;
      }
      if (stars > 0) {
        uint amount = balance * stars / totalStars; // CHECK DIVISION BY ZERO
        _beneficiaryRewards[beneficiary] += amount;
      }
    }

    for (uint i=0; i < _snook.totalSupply(); i++) {
      uint tokenId = _snook.tokenByIndex(i);
      address beneficiary = _snook.ownerOf(tokenId);
      uint amount = _beneficiaryRewards[beneficiary];
      if (amount > 0) {
        _beneficiaryRewards[beneficiary] = 0;
        _skill.transfer(beneficiary, amount);
      }
    }
  } 
}

contracts/Treasury.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";


import './SkillToken.sol';

contract Treasury is Ownable {

  using EnumerableSet for EnumerableSet.AddressSet;
  EnumerableSet.AddressSet private _allocatees;
  mapping (address => uint) private _percentages;
  mapping (address => uint) private _periodicities; // in secs
  mapping (address => uint) private _allocTimes;
  SkillToken private _skill;

  constructor(address skill) 
  {
    _skill = SkillToken(skill);
  }

  // remove constructor; add this function and removeAllocatee function; use enumarableSet
  function upsert(address allocatee, uint percentage, uint periodicity) public onlyOwner {
    require(_percentageSum() + percentage <= 100, "Invalid percentage");
    _allocatees.add(allocatee);
    _percentages[allocatee] = percentage;
    _periodicities[allocatee] = periodicity;
  }

  function remove(address allocatee) public onlyOwner {
    require(_allocatees.contains(allocatee), 'No such allocatee');
    _allocatees.remove(allocatee);
    delete _percentages[allocatee];
    delete _periodicities[allocatee];
    delete _allocTimes[allocatee];
  }

  function _percentageSum() public view returns (uint) {
    uint sum = 0;
    for (uint i=0; i<_allocatees.length(); i++) {
      address allocatee = _allocatees.at(i);
      sum += _percentages[allocatee];
    }
    return sum;
  }

  function allocate() public {
    uint balance = _skill.balanceOf(address(this));
    for (uint i=0; i<_allocatees.length(); i++) {
      address allocatee = _allocatees.at(i);
      if (_allocTimes[allocatee] + _periodicities[allocatee] * 1 seconds < block.timestamp) {
        uint amount = balance * _percentages[allocatee] / 100;
        _skill.transfer(allocatee, amount);
        _allocTimes[allocatee] = block.timestamp;
      }
    }
  }
}
